import React, { useState } from "react";
import styles from "./cadastro-aluno.module.css";
import Footer from "../../componets/layout/Footer";
import Navbar from "../../componets/layout/Navbar";
import api from "../../client/api";

function CadastroAluno() {
  const [ativo, setAtivo] = useState(true);
  const [nome, setNome] = useState("");
  const [ra, setRA] = useState("");
  const [curso, setCurso] = useState("");
  const [turma, setTurma] = useState("");
  const [ano, setAno] = useState("");
  const [message, setMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(true);
  const [aluno, setAluno] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    const alunoData = {
      aluno,
      ativo,
      nome,
      ra,
      curso,
      turma,
    };
    console.log(alunoData);

    api.aluno
      .create(alunoData)
      .then((response) => {
        console.log(response.data);
        setMessage("aluno cadastrado com sucesso");
        setIsSuccess(true);
      })
      .catch((error) => {
        console.log(error);
        setMessage("Erro ao cadastrar aluno");
        setIsSuccess(false);
      });
  };
  return (
    <div>
      <Navbar />
      <section className="grid">
        <div className={styles.login_container}>
          <h1>Cadastro de Aluno</h1>
          {message && (
            <div className={isSuccess ? "success-message" : "error-message"}>
              {message}
            </div>
          )}
          <form onSubmit={handleSubmit}>
            <div className={styles.input_container}>
              <label htmlFor="nome">Aluno:</label>
              <input
                type="text"
                name="nome"
                id="nome"
                autocomplete="off"
                placeholder="Nome do Aluno"
                value={nome}
                onChange={(e) => setNome(e.target.value)}
              />
            </div>
            <div className={styles.input_container}>
              <label htmlFor="ra">RA:</label>
              <input
                type="text"
                name="ra"
                id="ra"
                autocomplete="off"
                placeholder="RA do Aluno"
                value={ra}
                onChange={(e) => setRA(e.target.value)}
              />
            </div>
            <div className={styles.input_container}>
              <label htmlFor="curso">Curso:</label>
              <select id="DropCurso" value="" onChange={(e) => setCurso(e.target.value)}>
                <option value="Engenharia de Software">Engenharia de Software</option>
                <option value="Análise e Desenvolvimento de Sistemas">Análise e Desenvolvimento de Sistemas</option>
              </select>
            </div>
            <div className={styles.input_container}>
              <label htmlFor="turma">Turma:</label>
              <input
                type="text"
                name="turma"
                id="turma"
                autocomplete="off"
                placeholder="Turma do Aluno"
                value={turma}
                onChange={(e) => setTurma(e.target.value)}
              />
            </div>
            <button className={styles.btn_login} type="submit">
              Cadastrar
            </button>
          </form>
        </div>
      </section>
      <Footer />
    </div>
  );
}

export default CadastroAluno;
