import React, { useState } from "react";
import styles from "./cadastro-turma.module.css";
import Footer from "../../componets/layout/Footer";
import Navbar from "../../componets/layout/Navbar";
import api from "../../client/api";

function CadastroTurma() {
  const [ativo, setAtivo] = useState(true);
  const [turma, setTurma] = useState("");
  const [ano, setAno] = useState("");
  const [semestre, setSemestre] = useState("");
  const [message, setMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(true);

  const handleSubmit = (event) => {
    event.preventDefault();

    const alunoData = {
      turma,
      ativo,
      semestre,
      ano,
      curso
    };
    console.log(alunoData);

    api.aluno
      .create(alunoData)
      .then((response) => {
        console.log(response.data);
        setMessage("Turma cadastrada com sucesso");
        setIsSuccess(true);
      })
      .catch((error) => {
        console.log(error);
        setMessage("Erro ao cadastrar a Turma");
        setIsSuccess(false);
      });
  };
  return (
    <div>
      <Navbar />
      <section className="grid">
        <div className={styles.login_container}>
          <h1>Cadastro de Turma</h1>
          {message && (
            <div className={isSuccess ? "success-message" : "error-message"}>
              {message}
            </div>
          )}
          <form onSubmit={handleSubmit}>
            <div className={styles.input_container}>
              <label htmlFor="nome">Turma:</label>
              <input
                type="text"
                name="turma"
                id="turma"
                autocomplete="off"
                placeholder="Identificador da turma"
                value={turma}
                onChange={(e) => setTurma(e.target.value)}
              />
            </div>
            <div className={styles.input_container}>
              <label htmlFor="ra">Ano:</label>
              <input
                type="number"
                name="ano"
                id="ano"
                autocomplete="off"
                placeholder="Ano"
                value={ano}
                onChange={(e) => setAno(e.target.value)}
              />
            </div>
            <div className={styles.input_container}>
              <label htmlFor="curso">Semestre:</label>
              <select id="DropCurso" value={semestre} onChange={(e) => setSemestre(e.target.value)}>
                <option value="" disabled selected>
                  Selecione um curso
                </option>
                <option value="ENGENHARIA_DE_SOFTWARE">Engenharia de Software</option>
                <option value="ANALISE_E_DESENVOLVIMENTO_DE_SISTEMAS">An√°lise e Desenvolvimento de Sistemas</option>
              </select>
            </div>
            <button className={styles.btn_login} type="submit">
              Cadastrar
            </button>
          </form>
        </div>
      </section>
      <Footer />
    </div>
  );
}

export default CadastroTurma;
